import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { validateJWT, type AuthenticatedRequest } from "./middleware/validateJWT";
import { insertClientSchema, insertReviewRequestSchema, insertTestimonialSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Apply JWT validation to all /api/* routes
  app.use("/api", validateJWT);

  // Auth routes
  app.get("/api/auth/user", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      res.json(req.user);
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Failed to get user" });
    }
  });

  // Client routes
  app.get("/api/clients", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      const clients = await storage.getClients(req.user.id);
      res.json(clients);
    } catch (error) {
      console.error("Get clients error:", error);
      res.status(500).json({ message: "Failed to get clients" });
    }
  });

  app.post("/api/clients", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const validatedData = insertClientSchema.parse(req.body);
      const client = await storage.createClient(validatedData, req.user.id);
      res.status(201).json(client);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid client data", errors: error.errors });
      }
      console.error("Create client error:", error);
      res.status(500).json({ message: "Failed to create client" });
    }
  });

  app.put("/api/clients/:id", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const { id } = req.params;
      const validatedData = insertClientSchema.partial().parse(req.body);
      const client = await storage.updateClient(id, validatedData, req.user.id);

      if (!client) {
        return res.status(404).json({ message: "Client not found" });
      }

      res.json(client);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid client data", errors: error.errors });
      }
      console.error("Update client error:", error);
      res.status(500).json({ message: "Failed to update client" });
    }
  });

  app.delete("/api/clients/:id", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const { id } = req.params;
      const deleted = await storage.deleteClient(id, req.user.id);

      if (!deleted) {
        return res.status(404).json({ message: "Client not found" });
      }

      res.status(204).send();
    } catch (error) {
      console.error("Delete client error:", error);
      res.status(500).json({ message: "Failed to delete client" });
    }
  });

  // Review request routes
  app.get("/api/review-requests", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      const requests = await storage.getReviewRequests(req.user.id);
      res.json(requests);
    } catch (error) {
      console.error("Get review requests error:", error);
      res.status(500).json({ message: "Failed to get review requests" });
    }
  });

  app.post("/api/review-requests", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const validatedData = insertReviewRequestSchema.parse(req.body);
      const request = await storage.createReviewRequest(validatedData, req.user.id);
      res.status(201).json(request);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid request data", errors: error.errors });
      }
      console.error("Create review request error:", error);
      res.status(500).json({ message: "Failed to create review request" });
    }
  });

  app.put("/api/review-requests/:id/status", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const { id } = req.params;
      const { status } = req.body;

      if (!status || !["pending", "completed", "expired"].includes(status)) {
        return res.status(400).json({ message: "Invalid status" });
      }

      const request = await storage.updateReviewRequestStatus(id, status, req.user.id);

      if (!request) {
        return res.status(404).json({ message: "Review request not found" });
      }

      res.json(request);
    } catch (error) {
      console.error("Update review request status error:", error);
      res.status(500).json({ message: "Failed to update review request status" });
    }
  });

  // Testimonial routes
  app.get("/api/testimonials", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      const testimonials = await storage.getTestimonials(req.user.id);
      res.json(testimonials);
    } catch (error) {
      console.error("Get testimonials error:", error);
      res.status(500).json({ message: "Failed to get testimonials" });
    }
  });

  app.post("/api/testimonials", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const validatedData = insertTestimonialSchema.parse(req.body);
      const testimonial = await storage.createTestimonial(validatedData, req.user.id);
      res.status(201).json(testimonial);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid testimonial data", errors: error.errors });
      }
      console.error("Create testimonial error:", error);
      res.status(500).json({ message: "Failed to create testimonial" });
    }
  });

  // Dashboard stats route
  app.get("/api/dashboard/stats", async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      const stats = await storage.getDashboardStats(req.user.id);
      res.json(stats);
    } catch (error) {
      console.error("Get dashboard stats error:", error);
      res.status(500).json({ message: "Failed to get dashboard stats" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { 
  type User, type InsertUser,
  type Client, type InsertClient,
  type ReviewRequest, type InsertReviewRequest,
  type Testimonial, type InsertTestimonial,
  users, clients, reviewRequests, testimonials
} from "@shared/schema";
import { eq, and, desc } from "drizzle-orm";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL is required");
}

const sql = neon(process.env.DATABASE_URL);
const db = drizzle(sql);

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Clients
  getClients(userId: string): Promise<Client[]>;
  getClient(id: string, userId: string): Promise<Client | undefined>;
  createClient(client: InsertClient, userId: string): Promise<Client>;
  updateClient(id: string, client: Partial<InsertClient>, userId: string): Promise<Client | undefined>;
  deleteClient(id: string, userId: string): Promise<boolean>;

  // Review Requests
  getReviewRequests(userId: string): Promise<ReviewRequest[]>;
  getReviewRequest(id: string, userId: string): Promise<ReviewRequest | undefined>;
  createReviewRequest(request: InsertReviewRequest, userId: string): Promise<ReviewRequest>;
  updateReviewRequestStatus(id: string, status: string, userId: string): Promise<ReviewRequest | undefined>;

  // Testimonials
  getTestimonials(userId: string): Promise<Testimonial[]>;
  getTestimonial(id: string, userId: string): Promise<Testimonial | undefined>;
  createTestimonial(testimonial: InsertTestimonial, userId: string): Promise<Testimonial>;

  // Dashboard Stats
  getDashboardStats(userId: string): Promise<{
    totalClients: number;
    reviewsReceived: number;
    pendingRequests: number;
    avgRating: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.email, email)).limit(1);
    return result[0];
  }

  async createUser(user: InsertUser): Promise<User> {
    const result = await db.insert(users).values(user).returning();
    return result[0];
  }

  async getClients(userId: string): Promise<Client[]> {
    return await db.select().from(clients)
      .where(eq(clients.userId, userId))
      .orderBy(desc(clients.createdAt));
  }

  async getClient(id: string, userId: string): Promise<Client | undefined> {
    const result = await db.select().from(clients)
      .where(and(eq(clients.id, id), eq(clients.userId, userId)))
      .limit(1);
    return result[0];
  }

  async createClient(client: InsertClient, userId: string): Promise<Client> {
    const result = await db.insert(clients).values({ ...client, userId }).returning();
    return result[0];
  }

  async updateClient(id: string, client: Partial<InsertClient>, userId: string): Promise<Client | undefined> {
    const result = await db.update(clients)
      .set(client)
      .where(and(eq(clients.id, id), eq(clients.userId, userId)))
      .returning();
    return result[0];
  }

  async deleteClient(id: string, userId: string): Promise<boolean> {
    const result = await db.delete(clients)
      .where(and(eq(clients.id, id), eq(clients.userId, userId)))
      .returning();
    return result.length > 0;
  }

  async getReviewRequests(userId: string): Promise<ReviewRequest[]> {
    return await db.select().from(reviewRequests)
      .where(eq(reviewRequests.userId, userId))
      .orderBy(desc(reviewRequests.sentAt));
  }

  async getReviewRequest(id: string, userId: string): Promise<ReviewRequest | undefined> {
    const result = await db.select().from(reviewRequests)
      .where(and(eq(reviewRequests.id, id), eq(reviewRequests.userId, userId)))
      .limit(1);
    return result[0];
  }

  async createReviewRequest(request: InsertReviewRequest, userId: string): Promise<ReviewRequest> {
    const result = await db.insert(reviewRequests).values({ ...request, userId }).returning();
    return result[0];
  }

  async updateReviewRequestStatus(id: string, status: string, userId: string): Promise<ReviewRequest | undefined> {
    const result = await db.update(reviewRequests)
      .set({ status, completedAt: status === 'completed' ? new Date() : null })
      .where(and(eq(reviewRequests.id, id), eq(reviewRequests.userId, userId)))
      .returning();
    return result[0];
  }

  async getTestimonials(userId: string): Promise<Testimonial[]> {
    return await db.select().from(testimonials)
      .where(eq(testimonials.userId, userId))
      .orderBy(desc(testimonials.createdAt));
  }

  async getTestimonial(id: string, userId: string): Promise<Testimonial | undefined> {
    const result = await db.select().from(testimonials)
      .where(and(eq(testimonials.id, id), eq(testimonials.userId, userId)))
      .limit(1);
    return result[0];
  }

  async createTestimonial(testimonial: InsertTestimonial, userId: string): Promise<Testimonial> {
    const result = await db.insert(testimonials).values({ ...testimonial, userId }).returning();
    return result[0];
  }

  async getDashboardStats(userId: string): Promise<{
    totalClients: number;
    reviewsReceived: number;
    pendingRequests: number;
    avgRating: number;
  }> {
    const [clientsResult, testimonialsResult, requestsResult] = await Promise.all([
      db.select().from(clients).where(eq(clients.userId, userId)),
      db.select().from(testimonials).where(eq(testimonials.userId, userId)),
      db.select().from(reviewRequests).where(and(eq(reviewRequests.userId, userId), eq(reviewRequests.status, 'pending')))
    ]);

    const avgRating = testimonialsResult.length > 0 
      ? testimonialsResult.reduce((sum, t) => sum + t.rating, 0) / testimonialsResult.length
      : 0;

    return {
      totalClients: clientsResult.length,
      reviewsReceived: testimonialsResult.length,
      pendingRequests: requestsResult.length,
      avgRating: Math.round(avgRating * 10) / 10,
    };
  }
}

export const storage = new DatabaseStorage();

