                  {isLoading ? "Signing in..." : "Sign in"}
                </Button>
              </form>

              <div className="mt-8 pt-6 border-t border-slate-200 text-center">
                <p className="text-slate-600">
                  New to ReputaRank?{" "}
                  <a href="#" className="text-primary-600 hover:text-primary-700 font-medium">
                    Create an account
                  </a>
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

